package coderbyte.stringChallenge;

public class OptimizedCode {

    public static String StringChallenge(String str) {
        // reversing the input string
        final String challengeToken = "cverni1q5b";
        StringBuilder reversedConcatString = new StringBuilder(str).reverse().append(challengeToken);
        StringBuilder result = new StringBuilder();

        int strCount = 1;
        // change character at 3rd place
        for (int i = 0; i < reversedConcatString.length(); i++) {
            result.append((strCount % 3 == 0)? "X" : reversedConcatString.charAt(i));
            strCount = (strCount % 3 == 0)? 1 : strCount+1;
        }

        // Returning the final output to string
        return result.toString();
    }

    public static String StringChallenge2(String str) {
        // reversing the input string
        StringBuilder reversedInputString = new StringBuilder(str).reverse();

        // Defining Challenge token and initializing with given value (cverni1q5b)
        String challengeToken = "cverni1q5b";

        // Concatenating the reversed string with the ChallengeToken
        String resultOfConcatenation = reversedInputString.toString() + challengeToken;

        // Creating a StringBuilder to store the final output
        StringBuilder finalOutput = new StringBuilder();

        // Replacing every third character with 'X'
        for (int i = 0; i < resultOfConcatenation.length(); i++) {
            if ((i + 1) % 3 == 0) {
                finalOutput.append('X');
            } else {
                finalOutput.append(resultOfConcatenation.charAt(i));
            }
        }

        // Returning the final output to string
        return finalOutput.toString();
    }
    public static void main(String[] args) {
        String input = "coderbyte";
        String output = StringChallenge(input);
        System.out.println(output);

        testExecutionTime();
        testExecutionTime2();
    }

    static void testExecutionTime() {
        long startTime = System.currentTimeMillis();

        String result = StringChallenge("");
        long executionTime = System.currentTimeMillis() - startTime;

        System.out.println("Oddy executed in "+executionTime+"ms");
    }

    static void testExecutionTime2() {
        long startTime = System.currentTimeMillis();

        String result = StringChallenge2("");
        long executionTime = System.currentTimeMillis() - startTime;

        System.out.println("Charlse executed in "+executionTime+"ms");
    }
}